import { Link,useNavigate } from "react-router-dom";
import React, {useState} from "react";
import {useDispatch} from 'react-redux';
import {signup} from "../../redux/actions/auth";

import Box from '@mui/material/Box';
import Card from '@mui/material/Card';
import Typography from '@mui/material/Typography';
import Button from '@mui/material/Button';
import AppBar from '@mui/material/AppBar';
import { Toolbar } from "@mui/material";
import Grid from '@mui/material/Grid';



function Signup() {
	const nagivate = useNavigate();
    const dispatch = useDispatch();

    const [inputs, setInputs] = useState({
		username: '',
		email: '',
		password: '',
		confirmPassword: ''
	})

	const { username, email, password } = inputs;

	console.log(username, email, password);

    const onChange = (e) => {setInputs({ ...inputs, [e.target.name]: e.target.value})};

	const onSubmitForm = async (e) => {

        e.preventDefault();

        if (inputs.username !== "" && inputs.password !== "" && inputs.confirmPassword !== "" && inputs.email !== "" && inputs.password === inputs.confirmPassword && inputs.password.length >= 4) {
            try {
				const body = { username, email, password };
				const response = await fetch("http://localhost:808/users/signup", {
				  method: "POST",
				  headers: { "Content-Type": "application/json" },
				  body: JSON.stringify(body),
				});
				const parseRes = await response.json();
				console.log(parseRes)
				// localStorage.setItem("token", parseRes.token);
				// setAuth(true);
			  } catch (error) {
				console.error(error.message);
			  }
        }
    }

	const googleAuth = () => {
		window.open(
			`http://localhost:8080/auth/google/callback`,
			"_self"
		);
	};



	return (
		<div >
			<AppBar position="fixed">
            <Toolbar>
            <Typography variant="h6" noWrap component="div">
              Welcome!
            </Typography>

            </Toolbar>

          </AppBar>

			<Card variant="outlined" sx={{ mt:16, pt:3, mx:40, pb:40}} >

			<h1 >Sign up Form</h1>
			<div >
				<div >
				</div>
				<div >
					<h2 >Create Account</h2>


					<label className="px-2">
                  <h3 >Username:</h3>
                </label>
                <input
                  onChange={(e) => onChange(e)}
                  value={username}
                  type="text"
                  name="username"
                  class="mb-4"
                  placeholder="Enter a username"
                />


						<input  onChange={(e) => onChange(e)} type="text" value={email} placeholder="Email" name="email"/>

					<form onSubmit={onSubmitForm} >
						<input  onChange={(e) => onChange(e)} type="password" value={password} placeholder="Password" name="password"/>
						<input  onChange={(e) => onChange(e)} type="password" value={password} placeholder="retype your password" name="confirmPassword"/>

						<button  type="submit" >Sign Up</button>
					</form>

					<p >or</p>
					<button  onClick={googleAuth}>
						<span>Sing up with Google</span>
					</button>
					<p >
						Already Have Account ? <Link to="/login">Log In</Link>
					</p>
				</div>
			</div>
			</Card>
		</div>
	);
}

export default Signup;
